import React from "react";

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/coding-contest.jpg",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/hackathon.jpg",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/debugging-challenge.jpg",
  },
  {
    id: 4,
    type: "Non-Technical",
    name: "Photography Contest",
    description: "Showcase your photography skills and compete with others.",
    image: "/media/photography.jpg",
  },
  {
    id: 5,
    type: "Non-Technical",
    name: "Quiz",
    description: "Participate in a quiz competition and test your knowledge.",
    image: "/media/quiz.jpg",
  },
];

const Events = () => {
  return (
    <>
      <style>{`
        .events-container {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 50px;
          padding: 50px 0;
        }

        .event-card {
          display: flex;
          justify-content: center;
          align-items: center;
          width: 80%;
          height: 80vh;
          background-color: #f4f4f4;
          border-radius: 10px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
          overflow: hidden;
          position: relative;
        }

        .event-card.zigzag-left .event-image,
        .event-card.zigzag-right .event-image {
          width: 50%;
          position: relative;
          overflow: hidden;
        }

        .event-card.zigzag-left .event-image img,
        .event-card.zigzag-right .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          clip-path: polygon(0 0, 80% 0, 100% 100%, 0% 100%);
          transition: clip-path 0.5s ease-in-out;
        }

        .event-card.zigzag-right .event-image img {
          clip-path: polygon(20% 0, 100% 0, 100% 100%, 0 100%);
        }

        .event-card:hover .event-image img {
          clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
        }

        .event-details {
          width: 50%;
          padding: 30px;
          text-align: left;
          background-color: #ffffff;
        }

        .event-title {
          font-size: 2rem;
          margin-bottom: 20px;
        }

        .event-description {
          font-size: 1.2rem;
          margin-bottom: 30px;
        }

        .register-btn {
          padding: 10px 20px;
          background-color: #007bff;
          color: #fff;
          border: none;
          border-radius: 5px;
          cursor: pointer;
          font-size: 1rem;
        }

        .register-btn:hover {
          background-color: #0056b3;
        }
      `}</style>
      <div className="events-container">
        {events.map((event, index) => (
          <div
            className={`event-card ${index % 2 === 0 ? "zigzag-left" : "zigzag-right"}`}
            key={event.id}
          >
            <div className="event-image">
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h2 className="event-title">{event.name}</h2>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
