import React from "react";

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/image.png",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/image.png",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/image.png",
  },
];

const Events = () => {
  const handleLinkClick = (e) => {
    e.preventDefault();
    const targetId = e.currentTarget.getAttribute("href").substring(1);
    const targetElement = document.getElementById(targetId);

    if (targetElement) {
      targetElement.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
          background: black;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
        }

        .events-container {
          display: flex;
          flex-direction: column;
          height: 100vh; /* Ensure the container takes full height */
          overflow-y: auto; /* Allow scrolling */
        }

        .event-card {
          display: flex; 
          align-items: stretch; 
          height: 10000vh; /* Full height for each event */
          padding: 20px; 
          background: rgba(255, 255, 255, 0.1); 
          margin: 0; 
          border-radius: 10px; 
        }

        .event-image {
          width: 50%; 
          height: auto; 
          overflow: hidden; 
        }

        .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover; 
          border-radius: 10px; 
        }

        .event-details {
          flex: 1; 
          padding: 20px; 
          color: white;
          display: flex;
          flex-direction: column; 
          justify-content: center; 
        }

        .event-title {
          font-size: 2rem;
          font-weight: 700;
          margin: 0 0 10px 0; 
        }

        .event-description {
          font-size: 1.2rem;
          font-weight: 300;
          margin: 0 0 20px 0; 
        }

        .register-btn {
          padding: 12px 25px;
          background-color: white;
          color: black;
          border: none;
          border-radius: 10px;
          font-size: 1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
          align-self: flex-start; 
        }

        .register-btn:hover {
          background-color: black;
          color: white;
          border: 2px solid white;
        }
      `}</style>

      <div className="navbar">
        <a href="#home" onClick={handleLinkClick}>Home</a>
        <a href="#events" onClick={handleLinkClick}>Events</a>
        <a href="#schedule" onClick={handleLinkClick}>Schedule</a>
        <a href="#contact" onClick={handleLinkClick}>Contact</a>
      </div>

      <div className="events-container">
        {events.map((event) => (
          <div className="event-card" key={event.id}>
            <div className="event-image">
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
