import React from "react";

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "https://via.placeholder.com/800x600?text=Coding+Contest",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "https://via.placeholder.com/800x600?text=Hackathon",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "https://via.placeholder.com/800x600?text=Debugging+Challenge",
  },
  {
    id: 4,
    type: "Non-Technical",
    name: "Photography Contest",
    description: "Showcase your photography skills and compete with others.",
    image: "https://via.placeholder.com/800x600?text=Photography+Contest",
  },
  {
    id: 5,
    type: "Non-Technical",
    name: "Quiz",
    description: "Participate in a quiz competition and test your knowledge.",
    image: "https://via.placeholder.com/800x600?text=Quiz",
  },
];

const Events = () => {
  return (
    <>
      <style>{`
        .events-container {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 50px;
          padding: 0;
          margin: 0;
          width: 100vw;
          height: 100vh;
          overflow: hidden;
          background: rgba(0, 0, 0, 0.7);
          backdrop-filter: blur(10px);
        }

        .event-card {
          display: flex;
          justify-content: center;
          align-items: center;
          width: 90%;
          height: 90vh;
          margin: 20px;
          border-radius: 20px;
          box-shadow: 0 8px 15px rgba(0, 0, 0, 0.4);
          overflow: hidden;
          position: relative;
          background: rgba(0, 0, 0, 0.5);
          backdrop-filter: blur(15px);
          transition: transform 0.5s ease-in-out;
        }

        .event-card:hover {
          transform: scale(1.03);
        }

        .event-card.zigzag-left .event-image img {
          clip-path: polygon(0 0, 80% 0, 100% 100%, 0 100%);
        }

        .event-card.zigzag-right .event-image img {
          clip-path: polygon(20% 0, 100% 0, 100% 100%, 0 100%);
        }

        .event-card:hover .event-image img {
          clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
        }

        .event-image {
          width: 50%;
          height: 100%;
          overflow: hidden;
        }

        .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          transition: clip-path 0.5s ease-in-out;
        }

        .event-details {
          width: 50%;
          padding: 30px;
          color: white;
          text-align: center;
          display: flex;
          flex-direction: column;
          justify-content: center;
          font-family: "Poppins", sans-serif;
        }

        .event-title {
          font-size: 3rem;
          font-weight: 700;
          margin-bottom: 20px;
          text-transform: uppercase;
          letter-spacing: 2px;
        }

        .event-description {
          font-size: 1.5rem;
          font-weight: 300;
          margin-bottom: 30px;
        }

        .register-btn {
          padding: 15px 30px;
          background-color: rgba(255, 255, 255, 0.2);
          color: white;
          border: 2px solid white;
          border-radius: 10px;
          cursor: pointer;
          font-size: 1.2rem;
          font-weight: 500;
          text-transform: uppercase;
          transition: all 0.3s ease-in-out;
        }

        .register-btn:hover {
          background-color: white;
          color: black;
        }
      `}</style>
      <div className="events-container">
        {events.map((event, index) => (
          <div
            className={`event-card ${index % 2 === 0 ? "zigzag-left" : "zigzag-right"}`}
            key={event.id}
          >
            <div className="event-image">
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h2 className="event-title">{event.name}</h2>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
