import React, { useEffect, useRef } from "react";
import gsap from "gsap";

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/image.png",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/image.png",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/image.png",
  },
];

const Events = () => {
  const eventsRef = useRef([]);
  const containerRef = useRef(null);

  useEffect(() => {
    // GSAP Animation for event cards with more smooth transition
    eventsRef.current.forEach((event, index) => {
      gsap.fromTo(
        event,
        { opacity: 0, scale: 0.8, y: 100 }, // Cards start small and rise up
        {
          opacity: 1,
          scale: 1,
          y: 0,
          duration: 1.5,
          ease: "power2.out",
          delay: index * 0.4, // Stagger animation for cards
        }
      );
    });

    // GSAP background gradient animation for container
    gsap.to(containerRef.current, {
      background: "linear-gradient(45deg, #6a11cb, #2575fc)",
      duration: 3,
      ease: "power3.inOut",
    });
  }, []);

  const handleLinkClick = (e) => {
    e.preventDefault();
    const targetId = e.currentTarget.getAttribute("href").substring(1);
    const targetElement = document.getElementById(targetId);

    if (targetElement) {
      targetElement.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          font-family: 'Poppins', sans-serif;
          background: black;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
          margin: 0 10px;
        }

        .navbar a:hover {
          text-decoration: underline;
          color: #2575fc;
        }

        .events-container {
          display: flex;
          flex-direction: column;
          scroll-snap-type: y mandatory;
          overflow-y: scroll;
          height: 100vh;
        }

        .event-card {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          padding: 30px;
          margin: 20px;
          background: rgba(255, 255, 255, 0.1);
          border-radius: 15px;
          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
          transform: perspective(800px) rotateX(10deg); /* Slight tilt effect */
          transition: all 0.3s ease;
          overflow: hidden;
        }

        .event-card:hover {
          transform: scale(1.05) rotateX(0deg);
          box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
        }

        .event-image {
          width: 100%;
          height: 50%;
          overflow: hidden;
          border-radius: 15px;
        }

        .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          transition: all 0.3s ease;
        }

        .event-card:hover .event-image img {
          transform: scale(1.1);
        }

        .event-details {
          padding: 20px;
          text-align: center;
        }

        .event-title {
          font-size: 2.8rem;
          font-weight: 700;
          color: white;
          text-shadow: 3px 3px 10px rgba(0, 0, 0, 0.5);
          margin: 20px 0;
          opacity: 0;
          transform: translateY(30px);
        }

        .event-title.show {
          opacity: 1;
          transform: translateY(0);
          animation: fadeIn 1s forwards;
        }

        .event-description {
          font-size: 1.3rem;
          font-weight: 300;
          color: white;
          margin: 20px 0;
          opacity: 0;
          transform: translateY(30px);
        }

        .event-description.show {
          opacity: 1;
          transform: translateY(0);
          animation: fadeIn 1.2s forwards;
        }

        .register-btn {
          padding: 15px 30px;
          background-color: #2575fc;
          color: white;
          border: none;
          border-radius: 30px;
          font-size: 1.3rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          margin-top: 15px;
        }

        .register-btn:hover {
          background-color: #6a11cb;
          transform: translateY(-5px);
          box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @media (max-width: 768px) {
          .event-card {
            padding: 20px;
            margin: 10px;
          }

          .event-title {
            font-size: 2rem;
          }

          .event-description {
            font-size: 1.1rem;
          }

          .register-btn {
            font-size: 1rem;
          }
        }
      `}</style>

      <div className="navbar">
        <a href="#home" onClick={handleLinkClick}>Home</a>
        <a href="#events" onClick={handleLinkClick}>Events</a>
        <a href="#schedule" onClick={handleLinkClick}>Schedule</a>
        <a href="#contact" onClick={handleLinkClick}>Contact</a>
      </div>

      <div className="events-container" id="events" ref={containerRef}>
        {events.map((event, index) => (
          <div
            className="event-card"
            key={event.id}
            ref={(el) => (eventsRef.current[index] = el)}
          >
            <div className="event-image">
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className={`event-title ${index === 0 ? 'show' : ''}`}>{event.name}</h1>
              <p className={`event-description ${index === 0 ? 'show' : ''}`}>{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
