import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/image.png",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/image.png",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/image.png",
  },
];

const Events = () => {
  const eventsRef = useRef([]);
  const navbarRef = useRef(null);

  useEffect(() => {
    // Navbar animation on load
    gsap.fromTo(
      navbarRef.current,
      { y: -100, opacity: 0 },
      { y: 0, opacity: 1, duration: 1, ease: "power2.out" }
    );

    // GSAP Scroll Animations for event cards
    eventsRef.current.forEach((event, index) => {
      gsap.fromTo(
        event,
        { opacity: 0, y: 50 },
        {
          opacity: 1,
          y: 0,
          duration: 1.5,
          ease: "power2.out",
          scrollTrigger: {
            trigger: event,
            start: "top 80%", // Start animation when 80% of the card is visible
            end: "top 30%", // End animation before it completely leaves the viewport
            toggleActions: "play none none reverse",
          },
        }
      );
    });

    // Hover animation for buttons
    gsap.utils.toArray(".register-btn").forEach((btn) => {
      gsap.fromTo(
        btn,
        { scale: 1 },
        {
          scale: 1.1,
          duration: 0.3,
          ease: "power1.inOut",
          paused: true,
          repeat: -1,
          yoyo: true,
        }
      );
    });
  }, []);

  const handleLinkClick = (e) => {
    e.preventDefault();
    const targetId = e.currentTarget.getAttribute("href").substring(1);
    const targetElement = document.getElementById(targetId);

    if (targetElement) {
      targetElement.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
          background: black;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
          margin: 0 10px;
        }

        .navbar a:hover {
          text-decoration: underline;
        }

        .events-container {
          display: flex;
          flex-direction: column;
          scroll-snap-type: y mandatory; /* Enable full-page snapping */
          overflow-y: scroll;
          height: 100vh; /* Full viewport height for scrolling */
        }

        .event-card {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          height: 100vh; /* Full viewport height for each event */
          padding: 20px;
          background: rgba(255, 255, 255, 0.1);
          scroll-snap-align: start; /* Snap each card to the viewport */
          box-sizing: border-box;
          opacity: 0; /* Initial opacity for animation */
        }

        .event-image {
          width: 60%; /* Increased size */
          height: 50%; /* Increased height */
          overflow: hidden;
          transform: scale(0.95); /* Initial shrink for animation */
        }

        .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          border-radius: 10px;
          transition: transform 0.3s ease-in-out;
        }

        .event-image:hover img {
          transform: scale(1.1); /* Zoom effect on hover */
        }

        .event-details {
          padding: 20px;
          color: white;
          text-align: center;
          transform: translateY(30px); /* Initial offset for animation */
          opacity: 0;
        }

        .event-title {
          font-size: 2.5rem; /* Reduced font size */
          font-weight: 700;
          margin: 20px 0;
        }

        .event-description {
          font-size: 1.2rem; /* Reduced font size */
          font-weight: 300;
          margin: 20px 0;
        }

        .register-btn {
          padding: 15px 30px;
          background-color: white;
          color: black;
          border: none;
          border-radius: 10px;
          font-size: 1.2rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
        }

        .register-btn:hover {
          background-color: black;
          color: white;
          border: 2px solid white;
          transform: scale(1.1);
        }

        @media (max-width: 768px) {
          .event-image {
            width: 90%; /* Increased size for smaller screens */
            height: 50%;
          }

          .event-title {
            font-size: 1.8rem; /* Smaller text for mobile */
          }

          .event-description {
            font-size: 1rem;
          }

          .register-btn {
            font-size: 1rem;
          }
        }
      `}</style>

      <div className="navbar" ref={navbarRef}>
        <a href="#home" onClick={handleLinkClick}>
          Home
        </a>
        <a href="#events" onClick={handleLinkClick}>
          Events
        </a>
        <a href="#schedule" onClick={handleLinkClick}>
          Schedule
        </a>
        <a href="#contact" onClick={handleLinkClick}>
          Contact
        </a>
      </div>

      <div className="events-container" id="events">
        {events.map((event, index) => (
          <div
            className="event-card"
            key={event.id}
            ref={(el) => (eventsRef.current[index] = el)}
          >
            <div className="event-image">
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
