import React, { useEffect, useState } from "react";

const events = [
  {
    id: 1,
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
  },
  {
    id: 2,
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
  },
  {
    id: 3,
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
  },
];

const Events = () => {
  const [currentEvent, setCurrentEvent] = useState(0);

  const handleScroll = (event) => {
    const direction = event.deltaY > 0 ? 1 : -1;
    if (direction === 1 && currentEvent < events.length - 1) {
      setCurrentEvent((prev) => prev + 1);
    } else if (direction === -1 && currentEvent > 0) {
      setCurrentEvent((prev) => prev - 1);
    }
  };

  useEffect(() => {
    window.addEventListener("wheel", handleScroll);
    return () => {
      window.removeEventListener("wheel", handleScroll);
    };
  }, [currentEvent]);

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
          background: url('https://images.unsplash.com/photo-1506748686214-e9df14d4d9d0?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=MnwzNjUyOXwwfDF8c2VhcmNofDF8fHRyZWV8ZW58MHx8fHwxNjg2MzYzNzY0&ixlib=rb-4.0.3&q=80&w=1080') no-repeat center center fixed;
          background-size: cover;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
        }

        .event-card {
          height: 100vh;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          position: relative;
          opacity: 0;
          transform: translateY(50px);
          transition: all 0.5s ease;
        }

        .event-card.active {
          opacity: 1;
          transform: translateY(0);
        }

        .event-title {
          font-size: 3rem;
          font-weight: 700;
          color: white;
          margin: 0;
        }

        .event-description {
          font-size: 1.5rem;
          font-weight: 300;
          color: white;
          text-align: center;
        }

        .register-btn {
          padding: 12px 25px;
          background-color: white;
          color: black;
          border: none;
          border-radius: 10px;
          font-size: 1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
        }

        .register-btn:hover {
          background-color: black;
          color: white;
          border: 2px solid white;
        }
      `}</style>

      <div className="navbar">
        <a href="#home">Home</a>
        <a href="#events">Events</a>
        <a href="#schedule">Schedule</a>
        <a href="#contact">Contact</a>
      </div>

      <div className="events-container">
        {events.map((event, index) => (
          <div
            className={`event-card ${currentEvent === index ? "active" : ""}`}
            key={event.id}
          >
            <h1 className="event-title">{event.name}</h1>
            <p className="event-description">{event.description}</p>
            <button className="register-btn">Register Now</button>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
