import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import styled from "styled-components";

// Register ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/image.png",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/image.png",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/image.png",
  },
  {
    id: 4,
    type: "Technical",
    name: "Machine Learning Challenge",
    description: "Build machine learning models to solve data-related problems.",
    image: "/media/image.png",
  },
  {
    id: 5,
    type: "Technical",
    name: "App Development Challenge",
    description: "Create an app in a limited time using the latest frameworks and tools.",
    image: "/media/image.png",
  },
  {
    id: 6,
    type: "Non-Technical",
    name: "Quiz Bowl",
    description: "Test your knowledge on a wide range of topics in a fast-paced quiz format.",
    image: "/media/image.png",
  },
  {
    id: 7,
    type: "Non-Technical",
    name: "Public Speaking",
    description: "Deliver an impactful speech on a topic of your choice.",
    image: "/media/image.png",
  },
  {
    id: 8,
    type: "Non-Technical",
    name: "Photography Contest",
    description: "Showcase your photography skills with a theme-based competition.",
    image: "/media/image.png",
  },
  {
    id: 9,
    type: "Non-Technical",
    name: "Debate Championship",
    description: "Engage in structured debates on various contemporary issues.",
    image: "/media/image.png",
  },
  {
    id: 10,
    type: "Non-Technical",
    name: "Art and Craft Exhibition",
    description: "Display your creativity and artistic skills through various art forms.",
    image: "/media/image.png",
  },
];

const Events = () => {
  const eventsRef = useRef([]);

  useEffect(() => {
    // Loop through all the event cards and apply ScrollTrigger
    eventsRef.current.forEach((event, index) => {
      const direction = index % 2 === 0 ? "-100%" : "100%"; // Alternate direction (left or right)

      ScrollTrigger.create({
        trigger: event,
        start: "top 80%", // Start animation when card comes into view
        end: "bottom 30%",
        scrub: true,
        markers: false,
        onEnter: () => {
          gsap.to(event, {
            opacity: 1,
            x: 0, // Move to original position
            y: 0,
            scale: 1,
            rotation: 0,
            duration: 1.2,
            ease: "power4.out",
          });
        },
        onLeaveBack: () => {
          gsap.to(event, {
            opacity: 0.5,
            x: direction, // Move off-screen to left or right based on direction
            y: -100,
            scale: 0.95,
            rotation: 5,
            duration: 1.2,
            ease: "power4.out",
          });
        },
      });
    });
  }, []);

  return (
    <EventsContainer>
      <SectionTitle>Technical Events</SectionTitle>
      {events.slice(0, 5).map((event, index) => (
        <EventCard key={event.id} ref={(el) => (eventsRef.current[index] = el)}>
          <EventImage>
            <img src={event.image} alt={event.name} />
          </EventImage>
          <EventDetails>
            <EventTitle>{event.name}</EventTitle>
            <EventDescription>{event.description}</EventDescription>
            <RegisterBtn href="#register">Register Now</RegisterBtn>
          </EventDetails>
        </EventCard>
      ))}

      <SectionTitle>Non-Technical Events</SectionTitle>
      {events.slice(5).map((event, index) => (
        <EventCard key={event.id} ref={(el) => (eventsRef.current[index + 5] = el)}>
          <EventImage>
            <img src={event.image} alt={event.name} />
          </EventImage>
          <EventDetails>
            <EventTitle>{event.name}</EventTitle>
            <EventDescription>{event.description}</EventDescription>
            <RegisterBtn href="#register">Register Now</RegisterBtn>
          </EventDetails>
        </EventCard>
      ))}
    </EventsContainer>
  );
};

// Styled Components for inline CSS

const EventsContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
`;

const SectionTitle = styled.h2`
  font-size: 2em;
  margin-bottom: 20px;
  color: #333;
  text-align: center;
`;

const EventCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 20px;
  margin: 15px 0;
  background: rgba(255, 255, 255, 0.1);
  scroll-snap-align: start;
  opacity: 0; /* Initially hidden */
  transform: translateX(100%); /* Start off-screen */
  transition: transform 1.2s ease-out, opacity 1.2s ease-out;
  max-width: 300px;
  border-radius: 10px;
`;

const EventImage = styled.div`
  img {
    max-width: 100%;
    border-radius: 10px;
    margin-bottom: 15px;
  }
`;

const EventDetails = styled.div`
  text-align: center;
  color: #fff;
`;

const EventTitle = styled.h1`
  font-size: 1.5em;
  margin-bottom: 10px;
`;

const EventDescription = styled.p`
  font-size: 1em;
  margin-bottom: 15px;
`;

const RegisterBtn = styled.a`
  padding: 10px 20px;
  background-color: #4caf50;
  color: white;
  border-radius: 5px;
  text-decoration: none;
  font-weight: bold;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #45a049;
  }
`;

export default Events;
