import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const Events = () => {
  const eventsRef = useRef([]);

  const events = [
    {
      id: 1,
      name: "Technical Event 1",
      description: "Description for Technical Event 1.",
      image: "technical1.jpg",
    },
    {
      id: 2,
      name: "Technical Event 2",
      description: "Description for Technical Event 2.",
      image: "technical2.jpg",
    },
    {
      id: 3,
      name: "Technical Event 3",
      description: "Description for Technical Event 3.",
      image: "technical3.jpg",
    },
    {
      id: 4,
      name: "Technical Event 4",
      description: "Description for Technical Event 4.",
      image: "technical4.jpg",
    },
    {
      id: 5,
      name: "Technical Event 5",
      description: "Description for Technical Event 5.",
      image: "technical5.jpg",
    },
    {
      id: 6,
      name: "Non-Technical Event 1",
      description: "Description for Non-Technical Event 1.",
      image: "nontechnical1.jpg",
    },
    {
      id: 7,
      name: "Non-Technical Event 2",
      description: "Description for Non-Technical Event 2.",
      image: "nontechnical2.jpg",
    },
    {
      id: 8,
      name: "Non-Technical Event 3",
      description: "Description for Non-Technical Event 3.",
      image: "nontechnical3.jpg",
    },
    {
      id: 9,
      name: "Non-Technical Event 4",
      description: "Description for Non-Technical Event 4.",
      image: "nontechnical4.jpg",
    },
    {
      id: 10,
      name: "Non-Technical Event 5",
      description: "Description for Non-Technical Event 5.",
      image: "nontechnical5.jpg",
    },
  ];

  useEffect(() => {
    gsap.from(eventsRef.current, {
      opacity: 0,
      scale: 0.8,
      y: 50,
      duration: 0.8,
      stagger: 0.2,
      ease: "power3.out",
    });
  }, []);

  const handleImageHover = (e) => {
    gsap.to(e.currentTarget, {
      scale: 1.1,
      duration: 0.3,
    });
  };

  const handleImageLeave = (e) => {
    gsap.to(e.currentTarget, {
      scale: 1,
      duration: 0.3,
    });
  };

  return (
    <>
      <h1 className="main-title">Explore Our Events</h1>
      <div className="events-container">
        <h2 className="section-title">Technical Events</h2>
        {events.slice(0, 5).map((event, index) => (
          <div
            className="event-card"
            key={event.id}
            ref={(el) => (eventsRef.current[index] = el)}
          >
            <div
              className="event-image"
              onMouseEnter={handleImageHover}
              onMouseLeave={handleImageLeave}
            >
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}

        <h2 className="section-title">Non-Technical Events</h2>
        {events.slice(5).map((event, index) => (
          <div
            className="event-card"
            key={event.id}
            ref={(el) => (eventsRef.current[index + 5] = el)}
          >
            <div
              className="event-image"
              onMouseEnter={handleImageHover}
              onMouseLeave={handleImageLeave}
            >
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>

      <style jsx>{`
        .main-title {
          text-align: center;
          font-size: 2.5rem;
          margin: 20px 0;
          color: #333;
        }

        .section-title {
          text-align: center;
          font-size: 2rem;
          margin: 30px 0;
          color: #444;
        }

        .events-container {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
          gap: 20px;
          padding: 0 20px;
        }

        .event-card {
          background-color: white;
          box-shadow: 0px 10px 15px rgba(0, 0, 0, 0.1);
          border-radius: 10px;
          overflow: hidden;
          transition: transform 0.3s ease;
          display: flex;
          flex-direction: row;
          padding: 20px;
          align-items: center;
        }

        .event-card:hover {
          transform: translateY(-10px);
          box-shadow: 0px 20px 30px rgba(0, 0, 0, 0.15);
        }

        .event-image {
          flex-shrink: 0;
          width: 150px;
          height: 150px;
          overflow: hidden;
          border-radius: 8px;
          margin-right: 20px;
        }

        .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .event-details {
          flex-grow: 1;
        }

        .event-title {
          font-size: 1.6rem;
          margin: 10px 0;
          color: #222;
        }

        .event-description {
          font-size: 1rem;
          margin: 10px 0;
          color: #555;
        }

        .register-btn {
          background-color: #3d84e6;
          color: white;
          border: none;
          padding: 10px 20px;
          font-size: 1rem;
          cursor: pointer;
          border-radius: 5px;
          transition: background-color 0.3s ease;
        }

        .register-btn:hover {
          background-color: #2c6ab4;
        }
      `}</style>
    </>
  );
};

export default Events;
