import React from "react";

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "https://via.placeholder.com/600x400?text=Coding+Contest",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "https://via.placeholder.com/600x400?text=Hackathon",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "https://via.placeholder.com/600x400?text=Debugging+Challenge",
  },
];

const Events = () => {
  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
          background: linear-gradient(to bottom, rgba(0, 0, 0, 0.8), black);
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
        }

        .events-container {
          padding-top: 70px;
          display: flex;
          flex-direction: column;
          gap: 20px;
        }

        .event-card {
          display: flex;
          align-items: center;
          justify-content: space-between;
          width: 90%;
          margin: auto;
          padding: 20px;
          border-radius: 10px;
          background: rgba(255, 255, 255, 0.1);
          box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
          color: white;
          overflow: hidden;
          position: relative;
        }

        .event-image {
          width: 45%;
          position: relative;
          overflow: hidden;
          border-radius: 10px;
        }

        .event-image img {
          width: 100%;
          height: auto;
          border-radius: 10px;
          transition: transform 0.8s ease-in-out;
        }

        .event-card:hover .event-image img {
          transform: rotate(-5deg) scale(1.1);
        }

        .event-details {
          width: 50%;
          display: flex;
          flex-direction: column;
          justify-content: center;
          gap: 20px;
          text-align: right;
        }

        .event-title {
          font-size: 2.5rem;
          font-weight: 700;
          margin: 0;
        }

        .event-description {
          font-size: 1.2rem;
          font-weight: 300;
          line-height: 1.5;
        }

        .register-btn {
          align-self: flex-end;
          padding: 12px 25px;
          background-color: white;
          color: black;
          border: none;
          border-radius: 10px;
          font-size: 1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
        }

        .register-btn:hover {
          background-color: black;
          color: white;
          border: 2px solid white;
        }

        .event-name-overlay {
          position: absolute;
          bottom: 0;
          left: 0;
          width: 100%;
          text-align: center;
          background: rgba(0, 0, 0, 0.6);
          color: white;
          font-size: 1.5rem;
          font-weight: 600;
          padding: 10px 0;
          border-radius: 0 0 10px 10px;
        }
      `}</style>

      <div className="navbar">
        <a href="#home">Home</a>
        <a href="#events">Events</a>
        <a href="#schedule">Schedule</a>
        <a href="#contact">Contact</a>
      </div>

      <div className="events-container">
        {events.map((event) => (
          <div className="event-card" key={event.id}>
            <div className="event-image">
              <img src={event.image} alt={event.name} />
              <div className="event-name-overlay">{event.name}</div>
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
