import React, { useEffect } from "react";
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
import Events from "./Events";
import Navbar from "./Navbar";

function App() {
  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    // Setting up animations for zigzag image splits and event transitions
    const sections = document.querySelectorAll(".event-page");
    sections.forEach((section, index) => {
      const leftPart = section.querySelector(".left-image");
      const rightPart = section.querySelector(".right-image");

      gsap.fromTo(
        [leftPart, rightPart],
        { x: (i) => (i === 0 ? "-100%" : "100%"), opacity: 0 },
        {
          x: 0,
          opacity: 1,
          duration: 1,
          ease: "power2.out",
          scrollTrigger: {
            trigger: section,
            start: "top center",
            end: "bottom center",
            toggleActions: "play none none reverse",
          },
        }
      );

      gsap.to([leftPart, rightPart], {
        x: (i) => (i === 0 ? "-100%" : "100%"),
        duration: 1,
        ease: "power2.in",
        scrollTrigger: {
          trigger: section,
          start: "bottom center",
          end: "bottom bottom",
          toggleActions: "play none none reverse",
        },
      });
    });
  }, []);

  return (
    <div>
      <Navbar />
      <Events />
    </div>
  );
}

export default App;
