import React from "react";

const events = [
  {
    id: 1,
    type: "Technical",
    title: "Hackathon",
    description: "A 24-hour coding marathon to solve real-world challenges using innovative tech solutions.",
  },
  {
    id: 2,
    type: "Technical",
    title: "AI & Machine Learning Workshop",
    description: "Hands-on training on building machine learning models and understanding AI principles.",
  },
  {
    id: 3,
    type: "Technical",
    title: "Cybersecurity Challenge",
    description: "Compete to identify and fix vulnerabilities in a simulated environment.",
  },
  {
    id: 4,
    type: "Non-Technical",
    title: "Photography Contest",
    description: "Showcase your photography skills and win exciting prizes.",
  },
  {
    id: 5,
    type: "Non-Technical",
    title: "Art & Craft Workshop",
    description: "Explore creativity through hands-on activities like painting, origami, and more.",
  },
];

const Events = () => {
  return (
    <div>
      <style>
        {`
          .events-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 20px;
            padding: 20px;
          }

          .event-card {
            position: relative;
            max-width: 400px;
            padding: 20px;
            border: 1px solid #ccc;
            border-radius: 10px;
            background: #f9f9f9;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
            animation: zigzag 1s ease-in-out;
          }

          .event-card:nth-child(odd) {
            transform: translateX(-20px);
          }

          .event-card:nth-child(even) {
            transform: translateX(20px);
          }

          .event-title {
            font-size: 1.5rem;
            font-weight: bold;
            margin-bottom: 10px;
          }

          .event-description {
            font-size: 1rem;
            margin-bottom: 15px;
          }

          .register-btn {
            display: inline-block;
            padding: 10px 15px;
            background-color: #007bff;
            color: #fff;
            text-align: center;
            text-decoration: none;
            border-radius: 5px;
            transition: background-color 0.3s;
          }

          .register-btn:hover {
            background-color: #0056b3;
          }

          @keyframes zigzag {
            0% {
              transform: translateX(0);
            }
            50% {
              transform: translateX(10px);
            }
            100% {
              transform: translateX(0);
            }
          }
        `}
      </style>

      <div className="events-container">
        {events.map((event) => (
          <div className="event-card" key={event.id}>
            <div className="event-title">{event.title}</div>
            <div className="event-description">{event.description}</div>
            <a href="#" className="register-btn">
              Register Now
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
