import React, { useEffect, useRef } from "react";
import gsap from "gsap";

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/image.png",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/image.png",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/image.png",
  },
];

const Events = () => {
  const eventsRef = useRef([]);
  const containerRef = useRef(null);

  useEffect(() => {
    // GSAP Animation for event cards with zigzag slide transition
    eventsRef.current.forEach((event, index) => {
      gsap.fromTo(
        event,
        { opacity: 0, x: index % 2 === 0 ? -200 : 200, y: 100 }, // Cards come from different sides
        {
          opacity: 1,
          x: 0,
          y: 0,
          duration: 1.5,
          ease: "power2.out",
          delay: index * 0.5, // Stagger animation for cards
        }
      );
    });

    // GSAP background color animation for container
    gsap.to(containerRef.current, {
      background: "linear-gradient(to bottom, #1a1a2e, #16213e)",
      duration: 2,
      ease: "power2.inOut",
    });
  }, []);

  const handleLinkClick = (e) => {
    e.preventDefault();
    const targetId = e.currentTarget.getAttribute("href").substring(1);
    const targetElement = document.getElementById(targetId);

    if (targetElement) {
      targetElement.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
          background: black;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
          margin: 0 10px;
        }

        .navbar a:hover {
          text-decoration: underline;
        }

        .events-container {
          display: flex;
          flex-direction: column;
          scroll-snap-type: y mandatory;
          overflow-y: scroll;
          height: 100vh;
        }

        .event-card {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          height: 100vh;
          padding: 20px;
          background: rgba(255, 255, 255, 0.1);
          scroll-snap-align: start;
          box-sizing: border-box;
          transform: perspective(800px) rotateY(10deg); /* Slight tilt effect */
        }

        .event-image {
          width: 60%;
          height: 50%;
          overflow: hidden;
        }

        .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          border-radius: 10px;
          transition: transform 0.5s ease-in-out, rotate 0.3s ease-in-out;
        }

        .event-image img:hover {
          transform: scale(1.1);
          rotate: 10deg; /* Rotation effect */
        }

        .event-details {
          padding: 20px;
          color: white;
          text-align: center;
        }

        .event-title {
          font-size: 2.5rem;
          font-weight: 700;
          margin: 20px 0;
          opacity: 0;
        }

        .event-title.show {
          opacity: 1;
          transform: translateY(0);
          animation: fadeIn 1s forwards;
        }

        .event-description {
          font-size: 1.2rem;
          font-weight: 300;
          margin: 20px 0;
          opacity: 0;
        }

        .event-description.show {
          opacity: 1;
          transform: translateY(0);
          animation: fadeIn 1.2s forwards;
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .register-btn {
          padding: 15px 30px;
          background-color: white;
          color: black;
          border: none;
          border-radius: 10px;
          font-size: 1.2rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
        }

        .register-btn:hover {
          background-color: black;
          color: white;
          border: 2px solid white;
          transform: scale(1.05);
        }

        @media (max-width: 768px) {
          .event-image {
            width: 90%;
            height: 50%;
          }

          .event-title {
            font-size: 1.8rem;
          }

          .event-description {
            font-size: 1rem;
          }

          .register-btn {
            font-size: 1rem;
          }
        }
      `}</style>

      <div className="navbar">
        <a href="#home" onClick={handleLinkClick}>Home</a>
        <a href="#events" onClick={handleLinkClick}>Events</a>
        <a href="#schedule" onClick={handleLinkClick}>Schedule</a>
        <a href="#contact" onClick={handleLinkClick}>Contact</a>
      </div>

      <div className="events-container" id="events" ref={containerRef}>
        {events.map((event, index) => (
          <div
            className="event-card"
            key={event.id}
            ref={(el) => (eventsRef.current[index] = el)}
          >
            <div className="event-image">
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className={`event-title ${index === 0 ? 'show' : ''}`}>{event.name}</h1>
              <p className={`event-description ${index === 0 ? 'show' : ''}`}>{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
