import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import styled from "styled-components";

// Register ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const events = [
  // Technical Events
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/image1.png",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/image2.png",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/image3.png",
  },
  {
    id: 4,
    type: "Technical",
    name: "Machine Learning Challenge",
    description: "Build machine learning models to solve data-related problems.",
    image: "/media/image4.png",
  },
  {
    id: 5,
    type: "Technical",
    name: "App Development Challenge",
    description: "Create an app in a limited time using the latest frameworks and tools.",
    image: "/media/image5.png",
  },
  {
    id: 6,
    type: "Technical",
    name: "Web Development Contest",
    description: "Build a responsive and dynamic website in this timed challenge.",
    image: "/media/image6.png",
  },
  {
    id: 7,
    type: "Technical",
    name: "Data Science Contest",
    description: "Analyze and visualize data to provide insights in this competition.",
    image: "/media/image7.png",
  },
  {
    id: 8,
    type: "Technical",
    name: "AI Bot Challenge",
    description: "Develop a chatbot with AI capabilities to solve specific tasks.",
    image: "/media/image8.png",
  },
  {
    id: 9,
    type: "Technical",
    name: "Cloud Computing Workshop",
    description: "Learn and demonstrate skills in deploying cloud-based solutions.",
    image: "/media/image9.png",
  },
  {
    id: 10,
    type: "Technical",
    name: "Cybersecurity Challenge",
    description: "Secure a system by identifying and fixing vulnerabilities.",
    image: "/media/image10.png",
  },
  // Non-Technical Events
  {
    id: 11,
    type: "Non-Technical",
    name: "Quiz Bowl",
    description: "Test your knowledge on a wide range of topics in a fast-paced quiz format.",
    image: "/media/image11.png",
  },
  {
    id: 12,
    type: "Non-Technical",
    name: "Public Speaking",
    description: "Deliver an impactful speech on a topic of your choice.",
    image: "/media/image12.png",
  },
  {
    id: 13,
    type: "Non-Technical",
    name: "Photography Contest",
    description: "Showcase your photography skills with a theme-based competition.",
    image: "/media/image13.png",
  },
  {
    id: 14,
    type: "Non-Technical",
    name: "Debate Championship",
    description: "Engage in structured debates on various contemporary issues.",
    image: "/media/image14.png",
  },
  {
    id: 15,
    type: "Non-Technical",
    name: "Art and Craft Exhibition",
    description: "Display your creativity and artistic skills through various art forms.",
    image: "/media/image15.png",
  },
  {
    id: 16,
    type: "Non-Technical",
    name: "Cooking Contest",
    description: "Show off your culinary skills in a theme-based cooking competition.",
    image: "/media/image16.png",
  },
  {
    id: 17,
    type: "Non-Technical",
    name: "Treasure Hunt",
    description: "Solve riddles and clues to find the treasure in this fun activity.",
    image: "/media/image17.png",
  },
  {
    id: 18,
    type: "Non-Technical",
    name: "Dance Competition",
    description: "Showcase your talent in solo or group dance performances.",
    image: "/media/image18.png",
  },
  {
    id: 19,
    type: "Non-Technical",
    name: "Fashion Show",
    description: "Participate in a fashion showcase featuring unique styles and designs.",
    image: "/media/image19.png",
  },
  {
    id: 20,
    type: "Non-Technical",
    name: "Stand-up Comedy",
    description: "Perform and entertain with your comedic talent in front of a live audience.",
    image: "/media/image20.png",
  },
];

const Events = () => {
  const eventsRef = useRef([]);

  useEffect(() => {
    eventsRef.current.forEach((event, index) => {
      const direction = index % 2 === 0 ? "-100%" : "100%";

      gsap.fromTo(
        event,
        { opacity: 0, x: direction },
        {
          opacity: 1,
          x: 0,
          duration: 1.2,
          ease: "power4.out",
          scrollTrigger: {
            trigger: event,
            start: "top 80%",
            end: "bottom 30%",
            scrub: true,
            toggleActions: "play reverse play reverse",
          },
        }
      );
    });
  }, []);

  return (
    <EventsContainer>
      <SectionTitle>Technical Events</SectionTitle>
      {events.slice(0, 10).map((event, index) => (
        <EventCard key={event.id} ref={(el) => (eventsRef.current[index] = el)}>
          <EventImage>
            <img src={event.image} alt={event.name} />
          </EventImage>
          <EventDetails>
            <EventTitle>{event.name}</EventTitle>
            <EventDescription>{event.description}</EventDescription>
            <RegisterBtn href="#register">Register Now</RegisterBtn>
          </EventDetails>
        </EventCard>
      ))}

      <SectionTitle>Non-Technical Events</SectionTitle>
      {events.slice(10).map((event, index) => (
        <EventCard key={event.id} ref={(el) => (eventsRef.current[index + 10] = el)}>
          <EventImage>
            <img src={event.image} alt={event.name} />
          </EventImage>
          <EventDetails>
            <EventTitle>{event.name}</EventTitle>
            <EventDescription>{event.description}</EventDescription>
            <RegisterBtn href="#register">Register Now</RegisterBtn>
          </EventDetails>
        </EventCard>
      ))}
    </EventsContainer>
  );
};

// Styled Components
const EventsContainer = styled.div`
  background-color: #121212;
  color: #f5f5f5;
  padding: 40px;
  display: flex;
  flex-direction: column;
  gap: 50px;
  min-height: 100vh;
`;

const SectionTitle = styled.h2`
  font-size: 3rem;
  font-weight: bold;
  color: #f5f5f5;
  text-align: center;
  margin-bottom: 20px;
`;

const EventCard = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background: #1e1e1e;
  padding: 30px;
  border-radius: 15px;
  box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.5);
  opacity: 0;
  transform: translateX(100%);
  transition: all 0.3s ease;

  &:hover {
    background: #004e92;
    transform: scale(1.05);
    box-shadow: 0px 12px 24px rgba(0, 78, 146, 0.8);
  }
`;

const EventImage = styled.div`
  flex: 1.2;
  img {
    max-width: 100%;
    height: auto;
    border-radius: 10px;
    border: 2px solid #f5f5f5;
  }
`;

const EventDetails = styled.div`
  flex: 2;
  margin-left: 30px;
`;

const EventTitle = styled.h3`
  font-size: 2rem;
  margin-bottom: 15px;
  color: #d0d0d0;
`;

const EventDescription = styled.p`
  font-size: 1.4rem;
  margin-bottom: 25px;
  color: #c0c0c0;
`;

const RegisterBtn = styled.a`
  display: inline-block;
  padding: 15px 30px;
  background-color: #000;
  color: #f5f5f5;
  border-radius: 5px;
  text-decoration: none;
  font-size: 1.2rem;
  font-weight: bold;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #333;
  }
`;

export default Events;
