import React from "react";

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "https://via.placeholder.com/1920x1080?text=Coding+Contest",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "https://via.placeholder.com/1920x1080?text=Hackathon",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "https://via.placeholder.com/1920x1080?text=Debugging+Challenge",
  },
  {
    id: 4,
    type: "Non-Technical",
    name: "Photography Contest",
    description: "Showcase your photography skills and compete with others.",
    image: "https://via.placeholder.com/1920x1080?text=Photography+Contest",
  },
  {
    id: 5,
    type: "Non-Technical",
    name: "Quiz",
    description: "Participate in a quiz competition and test your knowledge.",
    image: "https://via.placeholder.com/1920x1080?text=Quiz",
  },
];

const Events = () => {
  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
        }

        .events-container {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 0;
          width: 100%;
          height: 100vh;
          overflow: hidden;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.8);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
        }

        .event-card {
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          width: 100%;
          height: 100vh;
          margin: 0;
          background: rgba(0, 0, 0, 0.8);
          color: white;
          text-align: center;
          position: relative;
          overflow: hidden;
        }

        .event-card .event-image {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: -1;
          overflow: hidden;
        }

        .event-card .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          filter: blur(3px) brightness(0.5);
          transition: transform 2s ease-in-out;
        }

        .event-card:hover .event-image img {
          transform: scale(1.1);
        }

        .event-details {
          z-index: 1;
          padding: 20px;
        }

        .event-title {
          font-size: 4rem;
          font-weight: 700;
          text-transform: uppercase;
          margin-bottom: 20px;
        }

        .event-description {
          font-size: 1.5rem;
          font-weight: 300;
          max-width: 800px;
          margin-bottom: 30px;
        }

        .register-btn {
          padding: 15px 30px;
          background-color: rgba(255, 255, 255, 0.2);
          color: white;
          border: 2px solid white;
          border-radius: 10px;
          cursor: pointer;
          font-size: 1.5rem;
          font-weight: 500;
          text-transform: uppercase;
          transition: all 0.3s ease-in-out;
        }

        .register-btn:hover {
          background-color: white;
          color: black;
        }
      `}</style>

      <div className="navbar">
        <a href="#home">Home</a>
        <a href="#events">Events</a>
        <a href="#schedule">Schedule</a>
        <a href="#contact">Contact</a>
      </div>

      <div className="events-container">
        {events.map((event) => (
          <div className="event-card" key={event.id}>
            <div className="event-image">
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
