import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/images/coding_contest.jpg", // Use a valid image path or URL
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/images/hackathon.jpg", // Use a valid image path or URL
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/images/debugging_challenge.jpg", // Use a valid image path or URL
  },
];

const Events = () => {
  const eventsRef = useRef([]);

  useEffect(() => {
    // Navbar Animation
    gsap.from(".navbar", {
      y: -50,
      opacity: 0,
      duration: 1,
      ease: "power2.out",
    });

    // Event Card Animations
    eventsRef.current.forEach((event, index) => {
      gsap.fromTo(
        event,
        { opacity: 0, y: 50 },
        {
          opacity: 1,
          y: 0,
          duration: 1,
          delay: index * 0.2,
          ease: "power2.out",
          scrollTrigger: {
            trigger: event,
            start: "top 80%",
            end: "bottom 60%",
            toggleActions: "play none none none",
          },
        }
      );
    });

    // Button Hover Effect
    gsap.utils.toArray(".register-btn").forEach((button) => {
      button.addEventListener("mouseenter", () => {
        gsap.to(button, {
          scale: 1.1,
          duration: 0.2,
          ease: "power2.out",
        });
      });
      button.addEventListener("mouseleave", () => {
        gsap.to(button, {
          scale: 1,
          duration: 0.2,
          ease: "power2.out",
        });
      });
    });
  }, []);

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
          background: black;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
          margin: 0 10px;
        }

        .navbar a:hover {
          text-decoration: underline;
        }

        .events-container {
          padding-top: 80px; /* Prevent overlap with navbar */
        }

        .event-card {
          margin: 50px auto;
          width: 80%;
          background: rgba(255, 255, 255, 0.1);
          border-radius: 15px;
          overflow: hidden;
          padding: 20px;
          text-align: center;
          transition: transform 0.3s ease-in-out;
        }

        .event-card:hover {
          transform: scale(1.05); /* Scale up on hover */
        }

        .event-image img {
          width: 100%;
          height: auto;
          border-radius: 10px;
        }

        .event-details {
          color: white;
          padding: 20px;
        }

        .event-title {
          font-size: 2rem;
          font-weight: bold;
          margin-bottom: 10px;
        }

        .event-description {
          font-size: 1.2rem;
          margin-bottom: 20px;
        }

        .register-btn {
          padding: 10px 20px;
          font-size: 1.2rem;
          font-weight: 600;
          color: black;
          background: white;
          border: none;
          border-radius: 5px;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
        }

        .register-btn:hover {
          background: black;
          color: white;
          border: 2px solid white;
        }
      `}</style>

      <div className="navbar">
        <a href="#home">Home</a>
        <a href="#events">Events</a>
        <a href="#contact">Contact</a>
      </div>

      <div className="events-container" id="events">
        {events.map((event, index) => (
          <div
            className="event-card"
            key={event.id}
            ref={(el) => (eventsRef.current[index] = el)}
          >
            <div className="event-image">
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
