import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

// Register ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const events = [
  // Technical Events
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/image.png",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/image.png",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/image.png",
  },
  {
    id: 4,
    type: "Technical",
    name: "Machine Learning Challenge",
    description: "Build machine learning models to solve data-related problems.",
    image: "/media/image.png",
  },
  {
    id: 5,
    type: "Technical",
    name: "App Development Challenge",
    description: "Create an app in a limited time using the latest frameworks and tools.",
    image: "/media/image.png",
  },

  // Non-Technical Events
  {
    id: 6,
    type: "Non-Technical",
    name: "Quiz Bowl",
    description: "Test your knowledge on a wide range of topics in a fast-paced quiz format.",
    image: "/media/image.png",
  },
  {
    id: 7,
    type: "Non-Technical",
    name: "Public Speaking",
    description: "Deliver an impactful speech on a topic of your choice.",
    image: "/media/image.png",
  },
  {
    id: 8,
    type: "Non-Technical",
    name: "Photography Contest",
    description: "Showcase your photography skills with a theme-based competition.",
    image: "/media/image.png",
  },
  {
    id: 9,
    type: "Non-Technical",
    name: "Debate Championship",
    description: "Engage in structured debates on various contemporary issues.",
    image: "/media/image.png",
  },
  {
    id: 10,
    type: "Non-Technical",
    name: "Art and Craft Exhibition",
    description: "Display your creativity and artistic skills through various art forms.",
    image: "/media/image.png",
  },
];

const Events = () => {
  const eventsRef = useRef([]);
  const containerRef = useRef(null);

  useEffect(() => {
    // Initialize GSAP ScrollTrigger for smooth scroll animation
    eventsRef.current.forEach((event, index) => {
      const direction = index % 2 === 0 ? 100 : -100; // Zigzag direction

      // Animate events on scroll with ScrollTrigger
      ScrollTrigger.create({
        trigger: event,
        start: "top 80%",
        end: "bottom 30%",
        scrub: true,
        markers: false,
        onEnter: () => {
          gsap.to(event, {
            opacity: 1,
            x: 0,
            y: 0,
            scale: 1,
            rotation: 0,
            duration: 1.2,
            ease: "power4.out",
          });
        },
        onLeaveBack: () => {
          gsap.to(event, {
            opacity: 0.5,
            x: direction,
            y: -100,
            scale: 0.95,
            rotation: 5,
            duration: 1.2,
            ease: "power4.out",
          });
        },
      });
    });

    // Background color animation for container
    gsap.to(containerRef.current, {
      background: "linear-gradient(to bottom, #16213e, #1a1a2e)",
      duration: 2,
      ease: "power2.inOut",
    });
  }, []);

  // Hover effect for images
  const handleImageHover = (e) => {
    gsap.to(e.target, { scale: 1.1, duration: 0.4, ease: "power2.out" });
  };

  const handleImageLeave = (e) => {
    gsap.to(e.target, { scale: 1, duration: 0.4, ease: "power2.out" });
  };

  const handleLinkClick = (e) => {
    e.preventDefault();
    const targetId = e.currentTarget.getAttribute("href").substring(1);
    const targetElement = document.getElementById(targetId);

    if (targetElement) {
      targetElement.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
          background: black;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: #ccc;
          font-size: 1.2rem;
          font-weight: 600;
          margin: 0 10px;
        }

        .navbar a:hover {
          text-decoration: underline;
          color: #fff;
        }

        .events-container {
          display: flex;
          flex-direction: column;
          scroll-snap-type: y mandatory;
          overflow-y: scroll;
          height: 100vh;
          position: relative;
        }

        .event-card {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          height: 100vh;
          padding: 20px;
          background: rgba(255, 255, 255, 0.1);
          scroll-snap-align: start;
          box-sizing: border-box;
          position: relative;
          transform: translateY(50px);
          box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);
          border-radius: 15px;
          transition: transform 0.4s ease-in-out, box-shadow 0.3s ease-in-out;
        }

        .event-card:hover {
          transform: translateY(-15px) scale(1.03);
          box-shadow: 0 8px 30px rgba(0, 0, 0, 0.8);
        }

        .event-image {
          width: 60%;
          height: 50%;
          overflow: hidden;
          border-radius: 10px;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.5);
          transition: transform 0.4s ease-in-out;
        }

        .event-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          transition: transform 0.4s ease-in-out;
        }

        .event-image img:hover {
          transform: scale(1.05);
        }

        .event-details {
          padding: 20px;
          color: #ccc;
          text-align: center;
          z-index: 10;
          display: flex;
          flex-direction: column;
          justify-content: flex-start;
          height: 100%;
        }

        .event-title {
          font-size: 2.5rem;
          font-weight: 700;
          margin: 10px 0;
          text-transform: uppercase;
          transition: color 0.3s ease-in-out;
        }

        .event-description {
          font-size: 1.1rem;
          font-family: 'Roboto', sans-serif;
          font-weight: 400;
          margin: 10px 0;
          line-height: 1.6;
          color: #ddd;
        }

        .register-btn {
          padding: 15px 30px;
          background-color: #333;
          color: #fff;
          border: none;
          border-radius: 10px;
          font-size: 1.2rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
          margin-top: 10px;
        }

        .register-btn:hover {
          background-color: #555;
          transform: scale(1.1);
          box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
        }

        .section-title {
          font-size: 2.5rem;
          font-weight: 600;
          color: #ccc;
          text-align: center;
          margin: 50px 0;
        }

        @media (max-width: 768px) {
          .event-image {
            width: 90%;
            height: 50%;
          }

          .event-title {
            font-size: 2rem;
          }

          .event-description {
            font-size: 1rem;
          }

          .register-btn {
            font-size: 1rem;
          }
        }
      `}</style>

      <div className="navbar">
        <a href="#home" onClick={handleLinkClick}>Home</a>
        <a href="#events" onClick={handleLinkClick}>Events</a>
        <a href="#schedule" onClick={handleLinkClick}>Schedule</a>
        <a href="#contact" onClick={handleLinkClick}>Contact</a>
      </div>

      <div className="events-container" id="events" ref={containerRef}>
        <h2 className="section-title">Technical Events</h2>
        {events.slice(0, 5).map((event, index) => (
          <div
            className="event-card"
            key={event.id}
            ref={(el) => (eventsRef.current[index] = el)}
          >
            <div
              className="event-image"
              onMouseEnter={handleImageHover}
              onMouseLeave={handleImageLeave}
            >
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}

        <h2 className="section-title">Non-Technical Events</h2>
        {events.slice(5).map((event, index) => (
          <div
            className="event-card"
            key={event.id}
            ref={(el) => (eventsRef.current[index + 5] = el)}
          >
            <div
              className="event-image"
              onMouseEnter={handleImageHover}
              onMouseLeave={handleImageLeave}
            >
              <img src={event.image} alt={event.name} />
            </div>
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
