import React from "react";

const technicalEvents = [
  {
    id: 1,
    title: "Hackathon",
    description: "Participate in a 24-hour hackathon to solve real-world challenges with innovative solutions.",
    image: "/images/hackathon.jpg",
  },
  {
    id: 2,
    title: "AI Workshop",
    description: "Hands-on training on AI and machine learning concepts with practical implementations.",
    image: "/images/ai_workshop.jpg",
  },
  {
    id: 3,
    title: "Cybersecurity Challenge",
    description: "Test your skills in identifying and fixing vulnerabilities in a simulated environment.",
    image: "/images/cybersecurity.jpg",
  },
  {
    id: 4,
    title: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/images/coding_contest.jpg",
  },
  {
    id: 5,
    title: "Robotics Challenge",
    description: "Showcase your robotic creations in an exciting robotics competition.",
    image: "/images/robotics.jpg",
  },
];

const nonTechnicalEvents = [
  {
    id: 6,
    title: "Photography Contest",
    description: "Capture the best moments and showcase your photography skills.",
    image: "/images/photography.jpg",
  },
  {
    id: 7,
    title: "Art & Craft Workshop",
    description: "Unleash your creativity through art and craft activities.",
    image: "/images/art_craft.jpg",
  },
  {
    id: 8,
    title: "Treasure Hunt",
    description: "Solve clues and find treasures in this adventurous event.",
    image: "/images/treasure_hunt.jpg",
  },
  {
    id: 9,
    title: "Debate Competition",
    description: "Showcase your debating skills and present your arguments on trending topics.",
    image: "/images/debate.jpg",
  },
  {
    id: 10,
    title: "Cooking Contest",
    description: "Cook and present your favorite dishes to impress the judges.",
    image: "/images/cooking.jpg",
  },
];

const Events = () => {
  const allEvents = [...technicalEvents, ...nonTechnicalEvents];

  return (
    <div>
      <style>
        {`
          body {
            margin: 0;
            font-family: Arial, sans-serif;
            overflow-x: hidden;
          }

          .event-page {
            display: flex;
            flex-direction: row;
            height: 100vh;
            width: 100%;
            position: relative;
          }

          .left-image,
          .right-image {
            width: 50%;
            height: 100%;
            position: absolute;
            top: 0;
            background-size: cover;
            background-position: center;
            background-repeat: no-repeat;
          }

          .left-image {
            left: 0;
          }

          .right-image {
            right: 0;
          }

          .content-container {
            width: 50%;
            padding: 20px;
            text-align: right;
            margin-left: auto;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: flex-end;
          }

          .event-title {
            font-size: 2.5rem;
            font-weight: bold;
            margin-bottom: 20px;
          }

          .event-description {
            font-size: 1.2rem;
            margin-bottom: 20px;
          }

          .register-btn {
            display: inline-block;
            padding: 15px 25px;
            font-size: 1rem;
            font-weight: bold;
            color: white;
            background-color: #007bff;
            border: none;
            border-radius: 5px;
            text-decoration: none;
            cursor: pointer;
            transition: background-color 0.3s ease;
          }

          .register-btn:hover {
            background-color: #0056b3;
          }
        `}
      </style>

      {allEvents.map((event, index) => (
        <div className="event-page" key={event.id}>
          <div
            className="left-image"
            style={{
              backgroundImage: `url(${event.image})`,
              clipPath: "polygon(0 0, 100% 0, 90% 100%, 0% 100%)",
            }}
          ></div>
          <div
            className="right-image"
            style={{
              backgroundImage: `url(${event.image})`,
              clipPath: "polygon(10% 0, 100% 0, 100% 100%, 0% 100%)",
            }}
          ></div>
          <div className="content-container">
            <h2 className="event-title">{event.title}</h2>
            <p className="event-description">{event.description}</p>
            <a href="#" className="register-btn">
              Register Now
            </a>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Events;
