import React from "react";

const events = [
  {
    id: 1,
    type: "Technical",
    name: "Coding Contest",
    description: "Compete in a timed coding contest and solve algorithmic challenges.",
    image: "/media/image.png",
  },
  {
    id: 2,
    type: "Technical",
    name: "Hackathon",
    description: "Develop innovative solutions to real-world problems in teams.",
    image: "/media/image.png",
  },
  {
    id: 3,
    type: "Technical",
    name: "Debugging Challenge",
    description: "Test your debugging skills to fix code issues within a time limit.",
    image: "/media/image.png",
  },
];

const Events = () => {
  const handleLinkClick = (e) => {
    e.preventDefault();
    const targetId = e.currentTarget.getAttribute("href").substring(1);
    const targetElement = document.getElementById(targetId);

    if (targetElement) {
      targetElement.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <>
      <style>{`
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
          font-family: 'Poppins', sans-serif;
          background: black;
        }

        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 60px;
          background-color: rgba(0, 0, 0, 0.9);
          display: flex;
          justify-content: space-between;
          align-items: center;
          z-index: 1000;
          padding: 0 20px;
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }

        .navbar a {
          text-decoration: none;
          color: white;
          font-size: 1.2rem;
          font-weight: 600;
          margin: 0 10px;
        }

        .navbar a:hover {
          text-decoration: underline;
        }

        .events-container {
          display: flex;
          flex-direction: column;
          scroll-snap-type: y mandatory;
          overflow-y: scroll;
          height: 100vh;
        }

        .event-card {
          position: relative;
          height: 100vh;
          width: 100%;
          display: flex;
          align-items: center;
          justify-content: center;
          scroll-snap-align: start;
          background-size: cover;
          background-position: center;
          background-repeat: no-repeat;
          background-blend-mode: overlay;
        }

        .event-card::before {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: rgba(0, 0, 0, 0.6);
          backdrop-filter: blur(8px);
          z-index: 1;
        }

        .event-details {
          position: relative;
          z-index: 2;
          text-align: center;
          color: white;
          padding: 20px;
        }

        .event-title {
          font-size: 4rem;
          font-weight: 900;
          margin: 0;
          text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.7);
        }

        .event-description {
          font-size: 1.5rem;
          font-weight: 300;
          margin: 20px 0;
          text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.6);
        }

        .register-btn {
          padding: 15px 30px;
          background-color: white;
          color: black;
          border: none;
          border-radius: 10px;
          font-size: 1.2rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
        }

        .register-btn:hover {
          background-color: black;
          color: white;
          border: 2px solid white;
        }

        @media (max-width: 768px) {
          .event-title {
            font-size: 2.5rem;
          }

          .event-description {
            font-size: 1rem;
          }

          .register-btn {
            font-size: 1rem;
          }
        }
      `}</style>

      <div className="navbar">
        <a href="#home" onClick={handleLinkClick}>Home</a>
        <a href="#events" onClick={handleLinkClick}>Events</a>
        <a href="#schedule" onClick={handleLinkClick}>Schedule</a>
        <a href="#contact" onClick={handleLinkClick}>Contact</a>
      </div>

      <div className="events-container" id="events">
        {events.map((event) => (
          <div
            className="event-card"
            key={event.id}
            style={{ backgroundImage: `url(${event.image})` }}
          >
            <div className="event-details">
              <h1 className="event-title">{event.name}</h1>
              <p className="event-description">{event.description}</p>
              <button className="register-btn">Register Now</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Events;
